lang["#MOD_CROWBAR_HEAD_SAW_NAME"] = "Монтировка: пильная насадка"
lang["#MOD_CROWBAR_HANDLE_HANDMADE_NAME"] = "Монтировка: ручная рукоять"
lang["#MOD_BBAT_HEAD_BARBED_NAME"] = "Бейсбольная бита: колючая проволока"
lang["#MOD_BBAT_HEAD_NAILS_NAME"] = "Бейсбольная бита: гвозди"
lang["#MOD_BBAT_HANDLE_TAPE_NAME"] = "Бейсбольная бита: лента для рукоятки"
lang["#MOD_MACHETE_BLADE_SERRATED_NAME"] = "Мачете: зазубренное лезвие"
